mapper.update:
update user_info
set user_name= #{userName},
    password=#{passWord},
    id_card=#{idCard}
where user_code=#{userCode};

update user_info
set delete_flag=1
where user_code=#{userCode};

mapper.select:
select user_name as userName, user_code as userCode, password as passWord, id_card as idCard
from user_info ui
where delete_flag =0

    and user_name = #{userName}


    and user_code = #{userCode}


    and password = #{passWord}


    and id_card = #{idCard}

limit 1

select user_name as userName, user_code as userCode, password as passWord, id_card as idCard
from user_info ui
where delete_flag =0;
